import React, { useState } from "react";
import Axios from "axios";
import Calendar from "react-calendar";

const POST_ADDRESS = "http://localhost:5000/orders/create";

const ModalForm = ({ modalClose }) => {
  //States
  const [date, setDate] = useState(new Date());

  // Functions
  const onChangeDate = (date) => {
    setDate(date);
  };

  const onSubmit = (event) => {
    event.preventDefault(event);

    const orderSubmission = {
      title: event.target.title.value,
      description: event.target.desc.value,
      quantity: event.target.quantity.value,
      deadline: date.toISOString(),
    };
    console.log(orderSubmission);

    // Axios.post(POST_ADDRESS, orderSubmission)
    //   .then(function (response) {
    //     console.log("Order created!");
    //     console.log(response.data);
    //   })
    //   .catch(function (error) {
    //     if (error.response) {
    //       // Request made and server responded
    //       console.log("Response Error");
    //       console.log(error.response.data.error.message);
    //     } else if (error.request) {
    //       // The request was made but no response was received
    //       console.log("No Response Error!");
    //       console.log(error.request);
    //     } else {
    //       console.log("Request Creation Error!");
    //       console.log("Error", error.message);
    //     }
    //   });

    modalClose();
  };

  return (
    <React.Fragment>
      <form onSubmit={onSubmit} className="form">
        <div className="form-group">
          <label>Title</label>
          <input
            id="title"
            type="text"
            required
            className="form-control"
            placeholder="title"
          />
        </div>
        <div className="form-group">
          <label>Description</label>
          <input
            id="desc"
            type="text"
            required
            className="form-control"
            placeholder="description"
          />
        </div>
        <div className="form-group">
          <label>Quantity</label>
          <input
            id="quantity"
            type="text"
            required
            className="form-control"
            placeholder="1"
          />
        </div>
        <div className="form-group">
          <label>Deadline</label>
          <div>
            <Calendar
              onChange={onChangeDate}
              value={date}
              calendarType="ISO 8601"
              minDate={new Date()}
            />
          </div>
        </div>
        <div className="form-group">
          <button
            type="submit"
            value="Create New Order"
            className="btn btn-primary"
          >
            Create New Order
          </button>
        </div>
      </form>
    </React.Fragment>
  );
};

export default ModalForm;
