import React, { useState } from "react";

const OrderContent = ({ orderObject }) => {
  // Functions
  const dateConverter = () => {
    const convDate = new Date(orderObject.deadline);
    const year = convDate.getUTCFullYear();
    const month = convDate.getUTCMonth();
    const day = convDate.getUTCDay();
    const finalDate = day + "-" + month + "-" + year;
    const UTC = convDate.getUTCDate();
    return finalDate;
  };

  return (
    <React.Fragment>
      <div className="accordion-content">
        <ul>
          <li>{orderObject.description}</li>
          <li>{orderObject.quantity}</li>
          <li>{dateConverter()}</li>
        </ul>
      </div>
    </React.Fragment>
  );
};

export default OrderContent;
