import React, { useState, useEffect } from "react";
import "./App.css";
import "bootstrap/dist/css/bootstrap.min.css";
import Axios from "axios";

// Import Modal Components
import ModalButton from "./components/modal/ModalButton";
import ModalOverlay from "./components/modal/ModalOverlay";
import TestButton from "./components/order/TestButton";

const ORDER_ADDRESS = "http://localhost:5000/orders/";

function App() {
  // States
  const [modalOpen, setModalOpen] = useState(false);
  const [polling, setPolling] = useState(false);

  // Order Display
  const [filter, setFilter] = useState("all");
  const [allOrders, setAllOrders] = useState([]);
  const [activeOrders, setActiveOrders] = useState([]);
  const [archivedOrders, setArchivedOrders] = useState([]);
  const [displayOrders, setDisplayOrders] = useState([]);

  // Functions

  useEffect(() => {
    const chooseDisplay = () => {
      Axios.get(ORDER_ADDRESS + filter).then((response) => {
        setDisplayOrders(response.data);
      });
    };
    chooseDisplay();
  }, [filter, polling]);

  return (
    <div className="App">
      <TestButton
        polling={polling}
        setPolling={setPolling}
        displayOrders={displayOrders}
      />
      <ModalButton setModalOpen={setModalOpen} />
      <ModalOverlay
        polling={polling}
        setPolling={setPolling}
        setModalOpen={setModalOpen}
        modalOpen={modalOpen}
      />
    </div>
  );
}

export default App;
