import React, { useState, useRef, useEffect } from "react";

const Order = ({ orderObject }) => {
  // States
  const [active, setActive] = useState(false);

  // Ref
  const contentRef = useRef(null);

  // Effects
  useEffect(() => {
    contentRef.current.style.maxHeight = active
      ? `${contentRef.current.scrollHeight}px`
      : "0px";
    contentRef.current.style.padding = active ? "0.5rem" : "0px";
  }, [contentRef, active]);

  // Functions
  const toggleActive = () => {
    setActive(!active);
  };

  return (
    <React.Fragment>
      <div className="accordion-section">
        <button className="accordion-title" onClick={toggleActive}>
          <p className="accordion-title-text">{orderObject.title}</p>
          <span className={active ? "accordion-icon rotate" : "accordion-icon"}>
            &lt;
          </span>
        </button>

        <div ref={contentRef} className="accordion-content">
          {orderObject.description}
        </div>
      </div>
    </React.Fragment>
  );
};

export default Order;
