import React, { useState } from "react";
import Axios from "axios";
import Calendar from "react-calendar";
import "react-calendar/dist/Calendar.css";

const ROOT_ADDRESS = "http://localhost:5000/orders/";

const PatchForm = ({ polling, setPolling, patchClose, orderObject }) => {
  //States
  const [date, setDate] = useState(new Date(orderObject.deadline));

  // Functions
  const onChangeDate = (date) => {
    setDate(date);
  };

  const patchMe = (event) => {
    event.preventDefault(event);

    // Submission Objects
    const titleSubmission = { title: event.target.title.value };
    const descSubmission = { description: event.target.desc.value };
    const quantitySubmission = { quantity: event.target.quantity.value };
    const deadlineSubmission = { deadline: date.toISOString() };

    const polishingSubmission = { polishing: event.target.polishing.checked };
    const sizingSubmission = { sizing: event.target.sizing.checked };
    const lazerSubmission = { lazer: event.target.lazer.checked };
    const engravingSubmission = { engraving: event.target.engraving.checked };
    const platingSubmission = { plating: event.target.plating.checked };
    const rhodiumSubmission = { rhodium: event.target.rhodium.checked };
    const cleaningSubmission = { cleaning: event.target.cleaning.checked };

    // Title
    Axios.patch(ROOT_ADDRESS + "/title/" + orderObject._id, titleSubmission)
      .then(function (response) {
        if (titleSubmission.title !== orderObject.title) {
          console.log("New title!");
          console.log(response.data);
        }
        setPolling(!polling);
      })
      .catch(function (error) {
        console.log(error);
      });

    // Description
    Axios.patch(ROOT_ADDRESS + "/desc/" + orderObject._id, descSubmission)
      .then(function (response) {
        if (descSubmission.description !== orderObject.description) {
          console.log("New description!");
          console.log(response.data);
        }
        setPolling(!polling);
      })
      .catch(function (error) {
        console.log(error);
      });

    // Quantity
    Axios.patch(
      ROOT_ADDRESS + "/quantity/" + orderObject._id,
      quantitySubmission
    )
      .then(function (response) {
        if (Number(quantitySubmission.quantity) !== orderObject.quantity) {
          console.log("New quantity!");
          console.log(response.data);
        }
        setPolling(!polling);
      })
      .catch(function (error) {
        console.log(error);
      });

    // Deadline
    Axios.patch(
      ROOT_ADDRESS + "/deadline/" + orderObject._id,
      deadlineSubmission
    )
      .then(function (response) {
        if (deadlineSubmission.deadline !== orderObject.deadline) {
          console.log("New deadline!");
          console.log(response.data);
        }
        setPolling(!polling);
      })
      .catch(function (error) {
        console.log(error);
      });

    patchClose();
  };

  return (
    <React.Fragment>
      <form onSubmit={patchMe}>
        <div>
          <label>Title</label>
          <input
            id="title"
            type="text"
            required
            defaultValue={orderObject.title}
          />
        </div>
        <div>
          <label>Description</label>
          <input
            id="desc"
            type="text"
            required
            defaultValue={orderObject.description}
          />
        </div>
        <div>
          <label>Quantity</label>
          <input
            id="quantity"
            type="text"
            required
            defaultValue={orderObject.quantity}
          />
        </div>
        <div>
          <label>
            Polishing
            <input type="checkbox" id="polishing"></input>
          </label>
        </div>
        <div>
          <label>
            Sizing
            <input type="checkbox" id="sizing"></input>
          </label>
        </div>
        <div>
          <label>
            Lazer
            <input type="checkbox" id="lazer"></input>
          </label>
        </div>
        <div>
          <label>
            Engraving
            <input type="checkbox" id="engraving"></input>
          </label>
        </div>
        <div>
          <label>
            Plating
            <input type="checkbox" id="plating"></input>
          </label>
        </div>
        <div>
          <label>
            Rhodium
            <input type="checkbox" id="rhodium"></input>
          </label>
        </div>
        <div>
          <label>
            Cleaning
            <input type="checkbox" id="cleaning"></input>
          </label>
        </div>
        <div>
          <label>Deadline</label>
          <div>
            <Calendar
              onChange={onChangeDate}
              value={date}
              calendarType="ISO 8601"
              minDate={new Date()}
            />
          </div>
        </div>
        <div>
          <button type="submit" value="Create New Order">
            Submit Edit
          </button>
        </div>
      </form>
    </React.Fragment>
  );
};

export default PatchForm;
