import React from "react";
import Modal from "react-modal";
import Axios from "axios";

const DELETE_ADDRESS = "http://localhost:5000/orders/delete/";
const ModalDelete = ({
  polling,
  setPolling,
  deleteOpen,
  setDeleteOpen,
  orderObject,
}) => {
  // Functions
  const deleteClose = () => {
    setDeleteOpen(false);
  };

  const deleteMe = () => {
    console.log(orderObject._id);
    Axios.delete(DELETE_ADDRESS + orderObject._id)
      .then(function (response) {
        console.log("Order deleted!");
        console.log(response.data);
        setPolling(!polling);
      })
      .catch(function (error) {
        console.log(error);
      });

    deleteClose();
  };

  return (
    <React.Fragment>
      <Modal
        isOpen={deleteOpen}
        onRequestClose={deleteClose}
        className="modal-background"
        overlayClassName="modal-overlay"
      >
        <div className="mymodal-content">
          <p>Are you sure you want to delete '{orderObject.title}'?</p>
          <button onClick={deleteMe}>Yes</button>
          <button onClick={deleteClose}>No</button>
        </div>
      </Modal>
    </React.Fragment>
  );
};

export default ModalDelete;
