import React, { useState, useEffect } from "react";
import "./App.css";
// import "bootstrap/dist/css/bootstrap.min.css";
import Axios from "axios";

// Import Modal Components
import PostModal from "./components/post/PostModal";

// Import Order Componenets
import OrderList from "./components/order/OrderList";
import NavBar from "./components/navbar/NavBar";
import Download from "./components/Download";
import FilterContainer from "./components/filter/FilterContainer";

const ORDER_ADDRESS = "http://localhost:5000/orders/";

function App() {
  // States
  const [modalOpen, setModalOpen] = useState(false);
  const [polling, setPolling] = useState(false);
  const [isDescending, setIsDescending] = useState(true);
  const [conditions, setConditions] = useState({
    polishing: false,
    sizing: false,
    lazer: false,
    engraving: false,
    plating: false,
    rhodium: false,
    cleaning: false,
  });

  // Order Display
  const [filter, setFilter] = useState("all");
  const [displayOrders, setDisplayOrders] = useState([]);

  // Effects
  useEffect(() => {
    // Compare array of objects to object of conditions
    // Filter for objects that fufill those conditions
    const godFilter = (arr) => {
      let arrFiltered = arr;
      let inAction = false;
      Object.entries(conditions).forEach((element) => {
        if (element[1] === true) {
          arrFiltered = arrFiltered.filter(function (order) {
            return order.todo[element[0]] === true;
          });
          // If we reach this code then we have filtered
          inAction = true;
        }
      });
      if (inAction) {
        console.log("filtered");
        return arrFiltered;
      }
      // If no conditions are ticked return default array
      else {
        console.log("unfiltered");
        return arr;
      }
    };

    const chooseDisplay = () => {
      Axios.get(ORDER_ADDRESS + filter)
        .then((response) => {
          // Filter the response before display
          setDisplayOrders(godFilter(response.data));
        })
        .catch(function (error) {
          console.log(error);
        });
    };
    chooseDisplay();
  }, [filter, conditions, polling]);

  // Functions
  // Sort for descending date
  const compareDateDesc = (a, b) => {
    if (a.date < b.date) return 1;
    if (a.date > b.date) return -1;
    return 0;
  };

  // Sort for ascending date
  const compareDateAsc = (a, b) => {
    if (a.date > b.date) return 1;
    if (a.date < b.date) return -1;
    return 0;
  };

  const godFilter = (arr) => {
    let arrFiltered = displayOrders;
    let inAction = false;
    Object.entries(arr).forEach((element) => {
      if (element[1] === true) {
        arrFiltered = arrFiltered.filter(function (order) {
          return order.todo[element[0]] === true;
        });
        inAction = true;
      }
    });
    if (inAction) {
      console.log(arrFiltered);
    } else {
      console.log(displayOrders);
    }
  };

  // TURN INTO CHECKBOX, USEEFFECT TO REAPPLY IF ACTIVE ON ANY POLLING
  const filterLazer = () => {
    let lazerDone = displayOrders.filter(function (order) {
      return order.todo.lazer === true;
    });
    setDisplayOrders(lazerDone);
  };

  const printDisplayArray = () => {
    console.log(displayOrders);
  };

  return (
    <div className="App">
      <button
        onClick={function () {
          godFilter(conditions);
        }}
      >
        GOD FILTER
      </button>
      <button onClick={filterLazer}>FILTER LAZER</button>
      <button onClick={printDisplayArray}>PRINT DISPLAY ARRAY</button>
      <Download />
      <FilterContainer
        conditions={conditions}
        setConditions={setConditions}
        setPolling={setPolling}
        polling={polling}
      />
      <PostModal
        polling={polling}
        setPolling={setPolling}
        setModalOpen={setModalOpen}
        modalOpen={modalOpen}
      />
      <NavBar
        setModalOpen={setModalOpen}
        setFilter={setFilter}
        isDescending={isDescending}
        setIsDescending={setIsDescending}
      />
      <OrderList
        polling={polling}
        setPolling={setPolling}
        displayOrders={
          isDescending
            ? displayOrders.sort(compareDateDesc)
            : displayOrders.sort(compareDateAsc)
        }
      />
    </div>
  );
}

export default App;
