import React, { useState, useEffect } from "react";
import "./App.css";
// import "bootstrap/dist/css/bootstrap.min.css";
import Axios from "axios";

// Import Modal Components
import ModalButton from "./components/modal/ModalButton";
import ModalOverlay from "./components/modal/ModalOverlay";

// Import Order Componenets
import FilterSelect from "./components/order/FilterSelect";
import OrderList from "./components/order/OrderList";

const ORDER_ADDRESS = "http://localhost:5000/orders/";

function App() {
  // States
  const [modalOpen, setModalOpen] = useState(false);
  const [polling, setPolling] = useState(false);

  // Order Display
  const [filter, setFilter] = useState("all");
  const [displayOrders, setDisplayOrders] = useState([]);

  // Effects
  useEffect(() => {
    const chooseDisplay = () => {
      Axios.get(ORDER_ADDRESS + filter).then((response) => {
        setDisplayOrders(response.data);
      });
    };
    chooseDisplay();
  }, [filter, polling]);

  // Functions
  const compareDate = (a, b) => {
    if (a.date < b.date) return -1;
    if (a.date < b.date) return 1;
    return 0;
  };

  return (
    <div className="App">
      <ModalButton setModalOpen={setModalOpen} />
      <ModalOverlay
        polling={polling}
        setPolling={setPolling}
        setModalOpen={setModalOpen}
        modalOpen={modalOpen}
      />
      <FilterSelect setFilter={setFilter} />
      <OrderList displayOrders={displayOrders} />
    </div>
  );
}

export default App;
