import React, { useState } from "react";

const OrderContent = ({ orderObject }) => {
  // Functions
  const dateConverter = (date) => {
    let convDate = new Date(date).toLocaleDateString();

    return convDate;
  };

  return (
    <React.Fragment>
      <div className="accordion-content">
        <ul>
          <li>DESCRIPTION : {orderObject.description}</li>
          <li>QUANTITY : {orderObject.quantity}</li>
          <li>CREATED CONV : {dateConverter(orderObject.date)}</li>
          <li>CREATED : {orderObject.date}</li>
          {/* <li>DEADLINE : {orderObject.deadline}</li> */}
        </ul>
      </div>
    </React.Fragment>
  );
};

export default OrderContent;
