import React, { useState } from "react";

const OrderContent = ({ orderObject }) => {
  // Functions
  const dateConverter = () => {
    let convDate = new Date(orderObject.deadline);
    const year = convDate.getUTCFullYear();
    const month = convDate.getUTCMonth();
    const day = convDate.getUTCDay();
    convDate = day + "-" + month + "-" + year;
    return convDate;
  };

  return (
    <React.Fragment>
      <div className="accordion-content">
        <ul>
          <li>DESCRIPTION : {orderObject.description}</li>
          <li>QUANTITY : {orderObject.quantity}</li>
          <li>DEADLINE : {dateConverter()}</li>
        </ul>
      </div>
    </React.Fragment>
  );
};

export default OrderContent;
